using System.Collections.Generic;using Project.EntenEller.Base.Scripts.Cache.Components.Master;using UnityEngine;namespace Project.Scripts.Builder.Building{    public class BuildingID : EEBehaviour    {        [SerializeField] private int id;        private static List<int> allID = new List<int>();        protected override void EEAwake()        {            base.EEAwake();            GenerateID();        }        public void GenerateID()        {            id = GetNextAvailableID();            allID.Add(id);         }                public void SetID(int newId)        {            if (allID.Contains(newId))            {                print("ID is repeating");                return;            }            id = newId;            allID.Add(id);        }                public int GetID()        {            return id;        }        private int GetNextAvailableID()        {            var nextId = 1;            while (allID.Contains(nextId))            {                nextId++;            }            return nextId;        }    }}