using System;using Project.EntenEller.Base.Scripts.Advanced.Behaviours.Loop;using Project.EntenEller.Base.Scripts.Advanced.Events;using UnityEngine;namespace Project.Scripts.Builder.Building{    public class BuildingRotatorInputKeyboard: EEBehaviourUpdate    {        public event Action RotateEvent;                protected override void EEUpdate()        {            base.EEUpdate();                        if(!Input.GetKeyDown(KeyCode.Space)) return;            Rotate();        }        public void Rotate()        {            RotateEvent.Call();        }    }}