using Project.EntenEller.Base.Scripts.Advanced.Spawns;using Project.EntenEller.Base.Scripts.Advanced.Tags;using Project.EntenEller.Base.Scripts.Cache.Components.Master;using Project.EntenEller.Base.Scripts.Patterns.Singleton;using Project.Scripts.SaveLoad;using Sirenix.OdinInspector;using Sirenix.Utilities;namespace Project.Scripts.Map{    public class MapClear : EEBehaviour    {        [Button]        public static void Clear()        {            EESingleton.Get<MapGridDrawer>().ClearMap();            var spawners = EETagUtils.FindEETagsInScenes("spawner");            spawners.ForEach(a => a.GetSelf<EESpawner>().DespawnAll());            EESingleton.Get<MapSaver>().DeleteAllBuildings();        }    }}