using System.IO;using Project.EntenEller.Base.Scripts.Cache.Components.Master;using Project.EntenEller.Base.Scripts.Patterns.Singleton;using UnityEngine;namespace Project.Scripts.SaveLoad{    public class MapLoaderFromComputer : EEBehaviour    {        [SerializeField] private string saveFileName = "map_data.json";         private MapLoader mapLoader;        protected override void EEAwake()        {            base.EEAwake();            mapLoader = EESingleton.Get<MapLoader>();            LoadFromLocalJson();         }        public void LoadFromLocalJson()        {            var filePath = Path.Combine(Application.persistentDataPath, saveFileName);            if (!File.Exists(filePath))            {                Debug.LogError($" file not found: Path {filePath}");                return;            }            try            {                var json = File.ReadAllText(filePath);                Debug.Log($"Success load from {filePath}");                                mapLoader.Load(json);            }            catch (System.Exception e)            {                Debug.LogError($"Fail {e.Message}");            }        }    }}