using System.Collections.Generic;using Project.EntenEller.Base.Scripts.Advanced.GameObjects;using Project.EntenEller.Base.Scripts.Advanced.Notifiers;using Project.EntenEller.Base.Scripts.Cache.Components.Master;using UnityEngine;namespace Project.Scripts.Skin{    public class CarSkinID : EEBehaviour    {        public int Index;        [SerializeField] private List<EEGameObject> allSkin;        private CarSkinView carSkinView;        protected override void EEAwake()        {            base.EEAwake();            carSkinView = GetSelf<CarSkinView>();        }        protected override void EEEnable()        {            base.EEEnable();            carSkinView.HideMesh(allSkin);        }        public void ApplyCarSkin(int id)        {            if (id != 0)            {                Index = id % allSkin.Count;                carSkinView.ViewSkin(allSkin[Index], allSkin);                return;            }                        carSkinView.HideMesh(allSkin);        }    }}