using System.Collections.Generic;using Project.EntenEller.Base.Scripts.Advanced.GameObjects;using Project.EntenEller.Base.Scripts.Advanced.Notifiers;using Project.EntenEller.Base.Scripts.Cache.Components.Master;namespace Project.Scripts.Skin{    public class CarSkinView : EEBehaviour    {        public EENotifier SuccesViewSkinNotifier;        public void HideMesh(List<EEGameObject> allSkin)        {          allSkin.ForEach(n => n.Off());        }        public void ViewSkin(EEGameObject car, List<EEGameObject> allSkin)        {            foreach (var skin in allSkin)            {                skin.SetState(skin == car);            }            SuccesViewSkinNotifier.Notify();        }    }}