using Project.EntenEller.Base.Scripts.Cache.Components.Master;using Project.EntenEller.Base.Scripts.Patterns.Singleton;using Project.EntenEller.Base.Scripts.UI.Text;using Project.Scripts.SaveLoad;using UnityEngine;namespace Project.Scripts.UINewMap{    public class NameMapManager: EEBehaviour    {        [SerializeField] private EEText textName;        private string nameMap;        protected override void EEAwake()        {            base.EEAwake();            EESingleton.Get<MapSaver>().SaveSuccessNotifier.Event += ShowName;        }        protected override void EEDestroy()        {            base.EEDestroy();            EESingleton.Get<MapSaver>().SaveSuccessNotifier.Event -= ShowName;        }        public void SetName(string text) =>  nameMap = text;        public void ShowName() => textName.SetData(nameMap);        public string GetName() => nameMap;    }}